/* eslint-disable react-hooks/exhaustive-deps */
import './App.css';
import React, { useState ,useEffect} from 'react';
import Cards from './Cards.js';
import Search from './Search.js';
import Container from 'react-bootstrap/Container';
import Spinner from 'react-bootstrap/Spinner';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [cards, setCards] = useState([]);
  const [loaded, setLoaded] = useState(false)
  const [page, setPage] = useState(0)
  const [keywords, setKeywords] = useState("")

  async function getData() {
    const response = await fetch(`https://api.elderscrollslegends.io/v1/cards/?pageSize=20&page=${page}&name=${keywords}`);
    const data = await response.json();
    if(cards.length>0 && data){
      setCards(cards.concat(data.cards))
    }else if(data){
      setCards(data.cards)
    }
  }

  const clicked = () => {
    setKeywords(document.getElementById("search").value)
    setCards([])
    setLoaded(false)
    setPage(0)
  }

  const onScroll = () => {
    if (window.pageYOffset+800 > document.body.scrollHeight) {
      setPage(page+1)
      getData()
    }
  };

  useEffect(() => {
    if(!loaded){
      getData()  
      setLoaded(true)
    }
    window.addEventListener("scroll", onScroll);
    return () => [window.removeEventListener("scroll", onScroll)]
  }, [page, keywords, loaded]);

  return (
    <Container className="App">
    <Spinner className="spinner" animation="grow" variant="primary"/>
      <Search clicked={clicked} />
      <Cards cards={cards}/>
      <div id="bottom"></div>
    </Container>
  );
}

export default App;
