/* eslint-disable react-hooks/exhaustive-deps */
import './App.css';
import React, { useState ,useEffect} from 'react';
import Cards from './Cards.js';
import Search from './Search.js';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [bottom, setBottom] = useState(false)
  const [cards, setCards] = useState([]);
  const [loaded, setLoaded] = useState(false)
  const [page, setPage] = useState(0)
  const [keywords, setKeywords] = useState("")

  async function getData() {
    const response = await fetch(`https://api.elderscrollslegends.io/v1/cards/?pageSize=20&page=${page}&name=${keywords}`);
    const data = await response.json();
    if(cards.length>0 && data){
      setCards(cards.concat(data.cards))
    }else if(data){
      setCards(data.cards)
    }
    setPage(page+1)
    console.log(data.cards)
  }

/*useEffect(()=>{
    if(!loaded){
      getData()      
    }
    return ()=>{
        setLoaded(true)
    } 
})*/
  const clicked = () => {
    setPage(0)
    setKeywords(document.getElementById("search").value)
    setCards([])
    setLoaded(false)
    //getData()
  }

  useEffect(() => {
    if(!loaded){
      getData()  
      setLoaded(true)    
    }
    const onScroll = () => {
      if (parseInt(window.pageYOffset)+600 > document.body.scrollHeight) {
        setBottom(true)
        getData()

      console.log("At The Bottom");
      }
    };
    window.addEventListener("scroll", onScroll);
    return () => [window.removeEventListener("scroll", onScroll), setLoaded(true)]
  }, [page, keywords, loaded]);

  return (
    <Container className="App">
      <Search clicked={clicked} />
      <Cards cards={cards}/>
      <div id="bottom"></div>
    </Container>
  );
}

export default App;
